    &lt;tr&gt; 
     &lt;td class=&quot;tableblock halign-left valign-top&quot;&gt;&lt;p class=&quot;tableblock&quot;&gt;thymeleaf&lt;/p&gt;&lt;/td&gt; 
     &lt;td class=&quot;tableblock halign-left valign-top&quot;&gt;&lt;p class=&quot;tableblock&quot;&gt;Thymeleaf templating engine&lt;/p&gt;&lt;/td&gt; 
    &lt;/tr&gt; 
    &lt;tr&gt; 
     &lt;td class=&quot;tableblock halign-left valign-top&quot;&gt;&lt;p class=&quot;tableblock&quot;&gt;web&lt;/p&gt;&lt;/td&gt; 
     &lt;td class=&quot;tableblock halign-left valign-top&quot;&gt;&lt;p class=&quot;tableblock&quot;&gt;Full-stack web development with Tomcat and Spring MVC&lt;/p&gt;&lt;/td&gt; 
    &lt;/tr&gt; 
   &lt;/tbody&gt; 
  &lt;/table&gt; 
  &lt;div class=&quot;listingblock&quot;&gt; 
   &lt;div class=&quot;title&quot;&gt;
    举个例子：通过CLI创建新项目
   &lt;/div&gt; 
   &lt;div class=&quot;content&quot;&gt; 
    &lt;pre&gt;$ spring init --name hello-world --artifactId hello-world --groupId org.springbooks --language java --boot-version 1.5.13.RELEASE --type maven-project --dependencies web,thymeleaf,devtools --extract&lt;/pre&gt; 
   &lt;/div&gt; 
  &lt;/div&gt; 
  &lt;div class=&quot;paragraph&quot;&gt; 
   &lt;p&gt;我们继续来看，创建的项目目录如下：&lt;/p&gt; 
  &lt;/div&gt; 
  &lt;div class=&quot;listingblock&quot;&gt; 
   &lt;div class=&quot;content&quot;&gt; 
    &lt;pre&gt;├── mvnw
├── mvnw.cmd
├── pom.xml
└── src
    ├── main
    │&amp;nbsp;&amp;nbsp; ├── java
    │&amp;nbsp;&amp;nbsp; │&amp;nbsp;&amp;nbsp; └── org
    │&amp;nbsp;&amp;nbsp; │&amp;nbsp;&amp;nbsp;     └── springbooks
    │&amp;nbsp;&amp;nbsp; │&amp;nbsp;&amp;nbsp;         └── helloworld
    │&amp;nbsp;&amp;nbsp; │&amp;nbsp;&amp;nbsp;             └── HelloWorldApplication.java
    │&amp;nbsp;&amp;nbsp; └── resources
    │&amp;nbsp;&amp;nbsp;     ├── application.properties
    │&amp;nbsp;&amp;nbsp;     ├── static
    │&amp;nbsp;&amp;nbsp;     └── templates
    └── test
        └── java
            └── org
                └── springbooks
                    └── helloworld
                        └── HelloWorldApplicationTests.java&lt;/pre&gt; 
   &lt;/div&gt; 
  &lt;/div&gt; 
  &lt;div class=&quot;paragraph&quot;&gt; 
   &lt;p&gt;一切就这么容易搞定，接下来看你的了。&lt;/p&gt; 
  &lt;/div&gt; 
 &lt;/div&gt; 
&lt;/div&gt;

